INSERT INTO newtable2(stats_dttm, product_tp_code, adgubun_tp_code, site_code, media_script_no, type , viewcnt, clickcnt) VALUES
(${I.yyyymmdd}, '${I.product}', '${I.adGubun}', '${I.siteCode}', '${I.scriptNo}', '${I.type}' , ${I.viewcnt1}, ${I.clickcnt})
ON CONFLICT (stats_dttm, product_tp_code, adgubun_tp_code, site_code, media_script_no, type) DO UPDATE SET viewcnt = newtable2.viewcnt + excluded.viewcnt , clickcnt = newtable2.clickcnt + excluded.clickcnt;

INSERT INTO test.NewTable(stats_dttm, product_tp_code, adgubun_tp_code, site_code, media_script_no, type , viewcnt, clickcnt) VALUES
(${I.yyyymmdd}, '${I.product}', '${I.adGubun}', '${I.siteCode}', '${I.scriptNo}', '${I.type}' , ${I.viewcnt1}, ${I.clickcnt})
ON DUPLICATE KEY UPDATE viewcnt = viewcnt + values(viewcnt), clickcnt = clickcnt+ values(clickcnt) ;
