mapper.select:
/* selectShopData : */
SELECT liveChk, PRDT_PRMCT
FROM ${tableName}
WHERE userid = #{advertiserId} AND pcode = #{pCode}

mapper.update:
/* sp_shop_info_NEW_W */ INSERT INTO SHOP_DATA(userid, url, pnm, pcode, price, imgpath, purl, gb, rdate, rtime , cate1, cate2, cate3, cate4, caid1, caid2, caid3, caid4 , LOADINFO, REGDATE, LASTUPDATE, width, height, STATUS, kakao_status , liveChk, PRDT_PRMCT ) VALUES
(#{I.advertiserId}, #{I.url}, #{I.pnm}, #{I.pCode}, #{I.price}, #{I.imgPath}, #{I.pUrl}, #{I.cwgb}, REPLACE(LEFT(NOW(),10),'-',''), '0101' , #{I.cate} , #{I.cate2} , '', '', '', '', #{I.CAID3}, #{I.CAID4} , 'U', NOW(), NOW(), #{I.width}, #{I.height}, #{I.status}, #{I.kakaoStatus} , #{I.liveChk}, #{I.prdtPrmct} )
ON DUPLICATE KEY UPDATE pnm=IF(liveChk='N', pnm, VALUES(pnm)) , price=IF(liveChk='N', price, VALUES(price)) , imgpath=IF(liveChk='N', imgpath, VALUES(imgpath)) , purl=IF(liveChk='N', purl, VALUES(purl)) , width=IF(liveChk='N', width, VALUES(width)) , height=IF(liveChk='N', height, VALUES(height)) , cate1=IF(liveChk='N', cate1, IF(VALUES(cate1) IN ('nocate','_unknown'),cate1,VALUES(cate1))) , cate2=IF(liveChk='N', cate2, IF(VALUES(cate2) IN ('nocate','_unknown'),cate2,VALUES(cate2))) , STATUS=IF(liveChk='N', STATUS, VALUES(STATUS)) , LOADINFO=IF(liveChk='N', LOADINFO, 'U') , CAID3 = IF (liveChk='N',CAID3, VALUES(CAID3)) , liveChk=IF(liveChk='N', liveChk, VALUES(liveChk)) , PRDT_PRMCT=IF(liveChk='N', PRDT_PRMCT , VALUES(PRDT_PRMCT)) , LASTUPDATE=IF(liveChk='N', LASTUPDATE, NOW());

